<?phpclass ControllerGlCreditInvoice extends HController {    protected $document_type_id = 24;    protected function getAlias() {        return 'gl/credit_invoice';    }    protected function getPrimaryKey() {        return 'credit_invoice_id';    }    protected function getList() {        parent::getList();        $this->data['action_ajax'] = $this->url->link($this->getAlias() . '/getAjaxLists', 'token=' . $this->session->data['token'], 'SSL');        $this->response->setOutput($this->render());    }    public function getAjaxLists() {        $lang = $this->load->language($this->getAlias());        $this->model[$this->getAlias()] = $this->load->model($this->getAlias());        $data = array();        $aColumns = array('action', 'document_date', 'document_identity', 'partner_type', 'partner_name', 'remarks', 'base_amount', 'created_at', 'check_box');        /*         * Paging         */        $sLimit = "";        if (isset($_GET['iDisplayStart']) && $_GET['iDisplayLength'] != '-1') {            $data['criteria']['start'] = $_GET['iDisplayStart'];            $data['criteria']['limit'] = $_GET['iDisplayLength'];        }        /*         * Ordering         */        $sOrder = "";        if (isset($_GET['iSortCol_0'])) {            $sOrder = " ORDER BY  ";            for ($i = 0; $i < intval($_GET['iSortingCols']); $i++) {                if ($_GET['bSortable_' . intval($_GET['iSortCol_' . $i])] == "true") {                    $sOrder .= "`" . $aColumns[intval($_GET['iSortCol_' . $i])] . "` " .                        ($_GET['sSortDir_' . $i] === 'asc' ? 'asc' : 'desc') . ", ";                }            }            $sOrder = substr_replace($sOrder, "", -2);            if ($sOrder == " ORDER BY") {                $sOrder = "";            }            $data['criteria']['orderby'] = $sOrder;        }        /*         * Filtering         * NOTE this does not match the built-in DataTables filtering which does it         * word by word on any field. It's possible to do here, but concerned about efficiency         * on very large tables, and MySQL's regex functionality is very limited         */        $arrWhere = array();        $arrWhere[] = "`company_id` = '".$this->session->data['company_id']."'";        $arrWhere[] = "`company_branch_id` = '".$this->session->data['company_branch_id']."'";        $arrWhere[] = "`fiscal_year_id` = '".$this->session->data['fiscal_year_id']."'";        if (isset($_GET['sSearch']) && $_GET['sSearch'] != "") {            $arrSSearch = array();            for ($i = 0; $i < count($aColumns); $i++) {                if (isset($_GET['bSearchable_' . $i]) && $_GET['bSearchable_' . $i] == "true" && $_GET['sSearch'] != '') {                    $arrSSearch[] = "LOWER(`" . $aColumns[$i] . "`) LIKE '%" . $this->db->escape(strtolower($_GET['sSearch'])) . "%'";                }            }            if(!empty($arrSSearch)) {                $arrWhere[] = '(' . implode(' OR ', $arrSSearch) . ')';            }        }        /* Individual column filtering */        for ($i = 0; $i < count($aColumns); $i++) {            if (isset($_GET['bSearchable_' . $i]) && $_GET['bSearchable_' . $i] == "true" && $_GET['sSearch_' . $i] != '') {                $arrWhere[] = "LOWER(`" . $aColumns[$i] . "`) LIKE '%" . $this->db->escape(strtolower($_GET['sSearch_' . $i])) . "%' ";            }        }        if (!empty($arrWhere)) {            //$data['filter']['RAW'] = substr($sWhere, 5, strlen($sWhere) - 5);            $data['filter']['RAW'] = implode(' AND ', $arrWhere);        }        //d($data, true);        $results = $this->model[$this->getAlias()]->getLists($data);        $iFilteredTotal = $results['total'];        $iTotal = $results['table_total'];        /*         * Output         */        $output = array(            "sEcho" => intval($_GET['sEcho']),            "iTotalRecords" => $iTotal,            "iTotalDisplayRecords" => $iFilteredTotal,            "aaData" => array()        );        foreach ($results['lists'] as $aRow) {            $row = array();            $actions = array();            $actions[] = array(                'text' => $lang['edit'],                'href' => $this->url->link($this->getAlias() . '/update', 'token=' . $this->session->data['token'] . '&' . $this->getPrimaryKey() . '=' . $aRow[$this->getPrimaryKey()], 'SSL'),                'btn_class' => 'btn btn-primary btn-xs',                'class' => 'fa fa-pencil'            );            $actions[] = array(                'text' => $lang['print'],                'target' => '_blank',                'href' => $this->url->link($this->getAlias() . '/printDocument', 'token=' . $this->session->data['token'] . '&' . $this->getPrimaryKey() . '=' . $aRow[$this->getPrimaryKey()], 'SSL'),                'btn_class' => 'btn btn-info btn-xs',                'class' => 'fa fa-print'            );            if($aRow['is_post']==0) {                $actions[] = array(                    'text' => $lang['post'],                    'href' => $this->url->link($this->getAlias() . '/post', 'token=' . $this->session->data['token'] . '&' . $this->getPrimaryKey() . '=' . $aRow[$this->getPrimaryKey()], 'SSL'),                    'btn_class' => 'btn btn-info btn-xs',                    'class' => 'fa fa-thumbs-up',                    'click'=> 'return confirm(\'Are you sure you want to post this item?\');'                );                $actions[] = array(                    'text' => $lang['delete'],                    'href' => 'javascript:void(0);',                    'click' => "ConfirmDelete('" . $this->url->link($this->getAlias() . '/delete', 'token=' . $this->session->data['token'] . '&id=' . $aRow[$this->getPrimaryKey()], 'SSL') . "')",                    'btn_class' => 'btn btn-danger btn-xs',                    'class' => 'fa fa-times'                );            }            $strAction = '';            foreach ($actions as $action) {                $strAction .= '<a '.(isset($action['target'])?'target="'.$action['target'].'"':'').' '.(isset($action['btn_class'])?'class="'.$action['btn_class'].'"':'').' href="' . $action['href'] . '" data-toggle="tooltip" title="' . $action['text'] . '" ' . (isset($action['click']) ? 'onClick="' . $action['click'] . '"' : '') . '>';                if (isset($action['class'])) {                    $strAction .= '<span class="' . $action['class'] . '"></span>';                } else {                    $strAction .= $action['text'];                }                $strAction .= '</a>&nbsp;';            }            for ($i = 0; $i < count($aColumns); $i++) {                if ($aColumns[$i] == 'action') {                    $row[] = $strAction;                } elseif ($aColumns[$i] == 'document_date') {                    $row[] = stdDate($aRow['document_date']);                } elseif ($aColumns[$i] == 'created_at') {                    $row[] = stdDateTime($aRow['created_at']);                } elseif ($aColumns[$i] == 'check_box') {                    if($aRow['is_post']==0) {                        $row[] = '<input type="checkbox" name="selected[]" value="' . $aRow[$this->getPrimaryKey()] . '" />';                    } else {                        $row[] = '';                    }                } else {                    $row[] = $aRow[$aColumns[$i]];                }            }            $output['aaData'][] = $row;        }        echo json_encode($output);    }    protected function getForm() {        parent::getForm();        $this->model['coa'] = $this->load->model('gl/coa_level3');        $this->data['coas'] = $this->model['coa']->getRows(array('company_id' => $this->session->data['company_id']));        $this->model['currency'] = $this->load->model('setup/currency');        $this->data['currencies'] = $this->model['currency']->getRows(array('company_id' => $this->session->data['company_id']));        $this->data['base_currency_id'] = $this->session->data['base_currency_id'];        $this->data['base_currency'] = $this->session->data['base_currency_name'];        $this->data['document_currency_id'] = $this->session->data['base_currency_id'];        $this->data['conversion_rate'] = "1.00";        $this->data['partner_types'] = $this->session->data['partner_types'];        $this->data['document_date'] = stdDate();        $this->data['document_identity'] = $this->data['lang']['auto'];        if (isset($this->request->get['credit_invoice_id']) && ($this->request->server['REQUEST_METHOD'] != 'POST')) {            $this->data['isEdit'] = 1;            $result = $this->model[$this->getAlias()]->getRow(array('credit_invoice_id' => $this->request->get['credit_invoice_id']));            foreach ($result as $field => $value) {                if ($field == 'document_date') {                    $this->data[$field] = stdDate($value);                } else {                    $this->data[$field] = $value;                }            }            $this->data['document_id'] = $this->request->get[$this->getPrimaryKey()];            $this->model['credit_invoice_detail'] = $this->load->model('gl/credit_invoice_detail');            $this->data['credit_invoice_details'] = $this->model['credit_invoice_detail']->getRows(array('credit_invoice_id' => $this->request->get['credit_invoice_id']), array('sort_order'));        }        $this->data['href_get_partner'] = $this->url->link('common/function/getPartner', 'token=' . $this->session->data['token']);        $this->data['action_validate_date'] = $this->url->link('common/function/validateDate', 'token=' . $this->session->data['token']);        $this->data['strValidation']= "{            'rules':{                'document_date': {'required':true, 'remote':  {url: '" . $this->data['action_validate_date'] . "', type: 'post'}},                'partner_type_id': {'required':true},                'partner_id': {'required':true},                'net_amount': {'required':true},            },            messages: {            document_date:{                remote: 'Invalid Date'            }}          }";        $this->response->setOutput($this->render());    }    protected function insertData($data) {        $this->model['document_type'] = $this->load->model('common/document_type');        $this->model['document'] = $this->load->model('common/document');        $this->model['partner'] = $this->load->model('common/partner');        $this->model['credit_invoice_detail'] = $this->load->model('gl/credit_invoice_detail');        $this->model['ledger'] = $this->load->model('gl/ledger');        $document = $this->model['document_type']->getNextDocument($this->document_type_id);        //d($document, true);        $data['document_type_id'] = $this->document_type_id;        $data['document_prefix'] = $document['document_prefix'];        $data['document_no'] = $document['document_no'];        $data['document_identity'] = $document['document_identity'];        $data['company_id'] = $this->session->data['company_id'];        $data['company_branch_id'] = $this->session->data['company_branch_id'];        $data['fiscal_year_id'] = $this->session->data['fiscal_year_id'];        $data['document_date'] = MySqlDate($data['document_date']);        $data['base_amount'] = $data['net_amount'] * $data['conversion_rate'];        //d($data, true);        $credit_invoice_id = $this->model[$this->getAlias()]->add($this->getAlias(), $data);        $insert_document = array(            'company_id' => $this->session->data['company_id'],            'company_branch_id' => $this->session->data['company_branch_id'],            'fiscal_year_id' => $this->session->data['fiscal_year_id'],            'document_type_id' => $this->document_type_id,            'document_id' => $credit_invoice_id,            'document_identity' => $data['document_identity'],            'document_date' => $data['document_date'],            'partner_type_id' => $data['partner_type_id'],            'partner_id' => $data['partner_id'],            'document_currency_id' => $data['document_currency_id'],            'document_amount' => $data['net_amount'],            'conversion_rate' => $data['conversion_rate'],            'base_currency_id' => $data['base_currency_id'],            'base_amount' => $data['base_amount'],        );        $document_id = $this->model['document']->add($this->getAlias(), $insert_document);        $partner = $this->model['partner']->getRow(array('partner_type_id' => $data['partner_type_id'], 'partner_id' => $data['partner_id']));        $gl_data[] = array(            'ref_document_type_id' => $this->document_type_id,            'ref_document_identity' => $data['document_identity'],            'coa_id' => $partner['outstanding_account_id'],            'remarks' => $data['remarks'],            'document_credit' => 0,            'document_debit' => $data['net_amount'],            'debit' => 0,            'credit' => ($data['net_amount'] * $data['conversion_rate']),        );        foreach ($data['credit_invoice_details'] as $sort_order => $detail) {            $detail['credit_invoice_id'] = $credit_invoice_id;            $detail['sort_order'] = $sort_order;            $detail['company_id'] = $this->session->data['company_id'];            $detail['company_branch_id'] = $this->session->data['company_branch_id'];            $detail['fiscal_year_id'] = $this->session->data['fiscal_year_id'];            $detail['partner_type_id'] = $data['partner_type_id'];            $detail['partner_id'] = $data['partner_id'];            $detail['document_currency_id'] = $data['document_currency_id'];            $detail['base_currency_id'] = $data['base_currency_id'];            $detail['conversion_rate'] = $data['conversion_rate'];            $detail['base_amount'] = $detail['amount'] * $data['conversion_rate'];            $credit_invoice_detail_id = $this->model['credit_invoice_detail']->add($this->getAlias(), $detail);            $gl_data[] = array(                'document_detail_id' => $credit_invoice_detail_id,                'coa_id' => $detail['coa_id'],                'remarks' => $detail['remarks'],                'document_debit' => 0,                'document_credit' => $detail['amount'],                'credit' => 0,                'debit' => ($detail['amount'] * $data['conversion_rate']),            );        }        foreach($gl_data as $sort_order => $ledger) {            $ledger['company_id'] = $this->session->data['company_id'];            $ledger['company_branch_id'] = $this->session->data['company_branch_id'];            $ledger['fiscal_year_id'] = $this->session->data['fiscal_year_id'];            $ledger['document_type_id'] = $this->document_type_id;            $ledger['document_id'] = $credit_invoice_id;            $ledger['document_identity'] = $data['document_identity'];            $ledger['document_date'] = $data['document_date'];            $ledger['sort_order'] = $sort_order;            $ledger['base_currency_id'] = $data['base_currency_id'];            $ledger['document_currency_id'] = $data['document_currency_id'];            $ledger['conversion_rate'] = $data['conversion_rate'];            $ledger['partner_type_id'] = $data['partner_type_id'];            $ledger['partner_id'] = $data['partner_id'];            $ledger['remarks'] = (isset($ledger['remarks'])?$ledger['remarks']:$data['remarks']);            $ledger_id = $this->model['ledger']->add($this->getAlias(), $ledger);        }        return $credit_invoice_id;    }    protected function updateData($primary_key, $data) {        $this->model['document_type'] = $this->load->model('common/document_type');        $this->model['document'] = $this->load->model('common/document');        $this->model['partner'] = $this->load->model('common/partner');        $this->model['credit_invoice_detail'] = $this->load->model('gl/credit_invoice_detail');        $this->model['ledger'] = $this->load->model('gl/ledger');        $this->model['document']->delete($this->getAlias(), $primary_key);        $this->model['ledger']->deleteBulk($this->getAlias(), array('company_id' => $this->session->data['company_id'],'document_type_id' => $this->document_type_id, 'document_id' => $primary_key));        $this->model['credit_invoice_detail']->deleteBulk($this->getAlias(), array('credit_invoice_id' => $primary_key));        $data['document_date'] = MySqlDate($data['document_date']);        $data['base_amount'] = $data['net_amount'] * $data['conversion_rate'];        $credit_invoice_id = $this->model[$this->getAlias()]->edit($this->getAlias(), $primary_key, $data);        $insert_document = array(            'company_id' => $this->session->data['company_id'],            'company_branch_id' => $this->session->data['company_branch_id'],            'fiscal_year_id' => $this->session->data['fiscal_year_id'],            'document_type_id' => $this->document_type_id,            'document_id' => $credit_invoice_id,            'document_identity' => $data['document_identity'],            'document_date' => $data['document_date'],            'partner_type_id' => $data['partner_type_id'],            'partner_id' => $data['partner_id'],            'document_currency_id' => $data['document_currency_id'],            'document_amount' => $data['net_amount'],            'conversion_rate' => $data['conversion_rate'],            'base_currency_id' => $data['base_currency_id'],            'base_amount' => $data['base_amount'],        );        $document_id = $this->model['document']->add($this->getAlias(), $insert_document);        $partner = $this->model['partner']->getRow(array('partner_type_id' => $data['partner_type_id'], 'partner_id' => $data['partner_id']));        $gl_data[] = array(            'ref_document_type_id' => $this->document_type_id,            'ref_document_identity' => $data['document_identity'],            'coa_id' => $partner['outstanding_account_id'],            'remarks' => $data['remarks'],            'document_credit' => 0,            'document_debit' => $data['net_amount'],            'debit' => 0,            'credit' => ($data['net_amount'] * $data['conversion_rate']),        );        foreach ($data['credit_invoice_details'] as $sort_order => $detail) {            $detail['credit_invoice_id'] = $credit_invoice_id;            $detail['sort_order'] = $sort_order;            $detail['company_id'] = $this->session->data['company_id'];            $detail['company_branch_id'] = $this->session->data['company_branch_id'];            $detail['fiscal_year_id'] = $this->session->data['fiscal_year_id'];            $detail['partner_type_id'] = $data['partner_type_id'];            $detail['partner_id'] = $data['partner_id'];            $detail['document_currency_id'] = $data['document_currency_id'];            $detail['base_currency_id'] = $data['base_currency_id'];            $detail['conversion_rate'] = $data['conversion_rate'];            $detail['base_amount'] = $detail['amount'] * $data['conversion_rate'];            $credit_invoice_detail_id = $this->model['credit_invoice_detail']->add($this->getAlias(), $detail);            $gl_data[] = array(                'document_detail_id' => $credit_invoice_detail_id,                'coa_id' => $detail['coa_id'],                'remarks' => $detail['remarks'],                'document_debit' => 0,                'document_credit' => $detail['amount'],                'credit' => 0,                'debit' => ($detail['amount'] * $data['conversion_rate']),            );        }        foreach($gl_data as $sort_order => $ledger) {            $ledger['company_id'] = $this->session->data['company_id'];            $ledger['company_branch_id'] = $this->session->data['company_branch_id'];            $ledger['fiscal_year_id'] = $this->session->data['fiscal_year_id'];            $ledger['document_type_id'] = $this->document_type_id;            $ledger['document_id'] = $credit_invoice_id;            $ledger['document_identity'] = $data['document_identity'];            $ledger['document_date'] = $data['document_date'];            $ledger['sort_order'] = $sort_order;            $ledger['base_currency_id'] = $data['base_currency_id'];            $ledger['document_currency_id'] = $data['document_currency_id'];            $ledger['conversion_rate'] = $data['conversion_rate'];            $ledger['partner_type_id'] = $data['partner_type_id'];            $ledger['partner_id'] = $data['partner_id'];            $ledger['remarks'] = (isset($ledger['remarks'])?$ledger['remarks']:$data['remarks']);            $ledger_id = $this->model['ledger']->add($this->getAlias(), $ledger);        }        return $credit_invoice_id;    }    protected function deleteData($primary_key) {        $this->model['document'] = $this->load->model('common/document');        $this->model['ledger'] = $this->load->model('gl/ledger');        $this->model['credit_invoice_detail'] = $this->load->model('gl/credit_invoice_detail');        $this->model['ledger']->deleteBulk($this->getAlias(), array('company_id' => $this->session->data['company_id'],'document_type_id' => $this->document_type_id, 'document_id' => $primary_key));        $this->model['credit_invoice_detail']->deleteBulk($this->getAlias(), array('credit_invoice_id' => $primary_key));        $this->model['document']->delete($this->getAlias(), $primary_key);        $this->model[$this->getAlias()]->delete($this->getAlias(), $primary_key);    }    public function printDocument() {        ini_set('max_execution_time',0);        ini_set('memory_limit',-1);        $lang = $this->load->language($this->getAlias());        $post = $this->request->post;        $session = $this->session->data;        $credit_invoice_id = $this->request->get['credit_invoice_id'];        $this->model['credit_invoice'] = $this->load->model('gl/credit_invoice');        $this->model['credit_invoice_detail'] = $this->load->model('gl/credit_invoice_detail');        $invoice = $this->model['credit_invoice']->getRow(array('credit_invoice_id' => $credit_invoice_id));        $details = $this->model['credit_invoice_detail']->getRows(array('credit_invoice_id' => $credit_invoice_id));        $pdf = new PDF('L', PDF_UNIT, 'A5', true, 'UTF-8', false);        // set document information        $pdf->SetCreator(PDF_CREATOR);        $pdf->SetAuthor('Huzaifa Khambaty');        $pdf->SetTitle('Credit Invoice');        $pdf->SetSubject('Credit Invoice');        //Set Header        $pdf->data = array(            'company_name' => $session['company_name'],            'report_name' => $lang['heading_title'],            'company_logo' => $session['company_image']        );        // set margins        //$pdf->SetMargins(PDF_MARGIN_LEFT, PDF_MARGIN_TOP, PDF_MARGIN_RIGHT);        $pdf->SetMargins(5, 35, 5);        $pdf->SetHeaderMargin(PDF_MARGIN_HEADER);        $pdf->SetFooterMargin(PDF_MARGIN_FOOTER);        // set auto page breaks        $pdf->SetAutoPageBreak(TRUE, PDF_MARGIN_BOTTOM);        // set font        $pdf->SetFont('helvetica', '', 10);        // add a page        $pdf->AddPage();       $pdf->Cell(25, 7, $lang['document_date'] .': ', 0, false, 'L', 0, '', 0, false, 'M', 'M');        $pdf->Cell(20, 7, stdDate($invoice['document_date']), 0, false, 'L', 0, '', 0, false, 'M', 'M');        $pdf->Cell(75, 7, '', 0, false, 'R', 0, '', 0, false, 'M', 'M');        $pdf->Cell(40, 7, $lang['document_no'] .':', 0, false, 'L', 0, '', 0, false, 'M', 'M');        $pdf->Cell(40, 7, $invoice['document_identity'], 0, false, 'L', 0, '', 0, false, 'M', 'M');        $pdf->ln(5);        $pdf->Cell(25, 7, $lang['partner_name'] .':', 0, false, 'L', 0, '', 0, false, 'M', 'M');        $pdf->Cell(20, 7, $invoice['partner_name'], 0, false, 'L', 0, '', 0, false, 'M', 'M');        $pdf->Cell(75, 7, '', 0, false, 'R', 0, '', 0, false, 'M', 'M');        $pdf->Cell(40, 7, $lang['manual_ref_no'] .':', 0, false, 'L', 0, '', 0, false, 'M', 'M');        $pdf->Cell(40, 7, $invoice['manual_ref_no'], 0, false, 'L', 0, '', 0, false, 'M', 'M');        $pdf->ln(5);        $pdf->Cell(25, 7, $lang['remarks'] .': ', 0, false, 'L', 0, '', 0, false, 'M', 'M');        $pdf->Cell(75, 7, $invoice['remarks'], 0, false, 'L', 0, '', 0, false, 'M', 'M');        $pdf->ln(10);        $pdf->SetFillColor(255, 255, 255);        $pdf->SetTextColor(0, 0, 0);        $pdf->Cell(80, 8, $lang['account'], 1, false, 'C', 1, '', 0, false, 'M', 'M');        $pdf->Cell(80, 8, $lang['remarks'], 1, false, 'C', 1, '', 0, false, 'M', 'M');        $pdf->Cell(30, 8, $lang['amount'], 1, false, 'C', 1, '', 0, false, 'M', 'M');        $pdf->SetFillColor(255, 255, 255);        $pdf->SetTextColor(0, 0, 0);        $total_amount = 0;        foreach($details as $detail) {            $pdf->ln(8);            $pdf->Cell(80, 8, $detail['account'], 'L', false, 'L', 0, '', 0, false, 'M', 'M');            $pdf->Cell(80, 8, $detail['remarks'], 'L', false, 'L', 0, '', 0, false, 'M', 'M');            $pdf->Cell(30, 8, number_format($detail['amount'],2), 'L,R', false, 'R', 0, '', 0, false, 'M', 'M');            $total_amount += $detail['amount'];        }        $x = $pdf->GetX();        $y = $pdf->GetY();        for ($i = $y; $i <= 90; $i++) {            $pdf->Ln(1);            $pdf->Cell(80, 8,'', 'L', false, 'C', 0, '', 1);            $pdf->Cell(80, 8, '', 'L', false, 'L', 0, '', 1);            $pdf->Cell(30, 8, '', 'L,R', false, 'R', 0, '', 1);            $y =$i;        }        $pdf->Ln(-1);        $pdf->Ln(5);        $pdf->Cell(190, 8, '', 'B', false, 'C', 0, '', 0, false, 'M', 'M');        $pdf->setXY($x,$y);        $pdf->ln(13);        $pdf->Cell(130, 8, 'IN WORD: ' . Number2Words(round($invoice['net_amount'],2)). ' only', 1, false, 'L', 0, '', 0, false, 'M', 'M');        $pdf->Cell(30, 8, $lang['total_amount'].': ', 'LTB', false, 'R', 0, '', 0, false, 'M', 'M');        $pdf->Cell(30, 8, number_format($total_amount,2), 'RTB', false, 'R', 0, '', 0, false, 'M', 'M');        //Close and output PDF document        $pdf->Output('Credit Invoice - '.$invoice['document_identity'].'.pdf', 'I');    }}class PDF extends TCPDF {    public $data = array();    //Page header    public function Header() {        // Logo        if($this->data['company_logo'] != '') {            $image_file = DIR_IMAGE.$this->data['company_logo'];            //$this->Image($file, $x='', $y='', $w=0, $h=0, $type='', $link='', $align='', $resize=false, $dpi=300, $palign='', $ismask=false, $imgmask=false, $border=0, $fitbox=false, $hidden=false, $fitonpage=false);            $this->Image($image_file, 10, 10, 30, '', 'JPG', '', 'T', false, 300, '', false, false, 0, false, false, false);        }        // Set font        $this->SetFont('helvetica', 'B', 20);        $this->Ln(2);        // Title        $this->Cell(0, 10, $this->data['company_name'], 0, false, 'C', 0, '', 0, false, 'M', 'M');        $this->Ln(10);        $this->Cell(0, 10, $this->data['report_name'], 0, false, 'C', 0, '', 0, false, 'M', 'M');    }    // Page footer    public function Footer() {        // Position at 15 mm from bottom        $this->SetY(-15);        // Set font        $this->SetFont('helvetica', 'I', 8);        // Page number        $this->Cell(0, 10, 'Page '.$this->getAliasNumPage().'/'.$this->getAliasNbPages(), 0, false, 'C', 0, '', 0, false, 'T', 'M');    }}?>