      $gl_data[] = array(                    'field' => $field,                    'ref_document_type_id' => $this->document_type_id,                    'ref_document_id' => $work_order_id,                    'ref_document_identity' => $data['document_identity'],                    'coa_id' => $freight_account_id,                    'document_debit' => $value,                    'document_credit' => 0,                    'debit' => $value,                    'credit' => 0                );            }            else if($field == "sales_tax_amount")            {                $totalDebit += $value;                $gl_data[] = array(                    'field' => $field,                    'ref_document_type_id' => $this->document_type_id,                    'ref_document_id' => $work_order_id,                    'ref_document_identity' => $data['document_identity'],                    'coa_id' => $sale_tax_account_id,                    'document_debit' => $value,                    'document_credit' => 0,                    'debit' => $value,                    'credit' => 0                );            }            else if($field == "income_tax_amount")            {                $totalDebit += $value;                $gl_data[] = array(                    'field' => $field,                    'ref_document_type_id' => $this->document_type_id,                    'ref_document_id' => $work_order_id,                    'ref_document_identity' => $data['document_identity'],                    'coa_id' => $income_tax_account_id,                    'document_debit' => $value,                    'document_credit' => 0,                    'debit' => $value,                    'credit' => 0                );            }            $this->model['work_order_commodity']->add($this->getAlias(), $dataDetail);        }        foreach ($data['work_order_details'] as $sort_order => $detail) {            $detail['work_order_id'] = $work_order_id;            $detail['sort_order'] = $sort_order;            $work_order_detail_id = $this->model['work_order_detail']->add($this->getAlias(), $detail);            $totalCredit += $detail['credit'];            $totalDebit += $detail['debit'];            $gl_data[] = array(                'document_detail_id' => $work_order_detail_id,                'ref_document_type_id' => $data['ref_document_type_id'],                'ref_document_id' => $data['ref_document_id'],                'ref_document_identity' => $data['ref_document_identity'],                'coa_id' => $detail['coa_id'],                'document_debit' => $detail['debit'],                'document_credit' => $detail['credit'],                'debit' => $detail['debit'],                'credit' => $detail['credit'],            );        }        $totalPayable = $totalDebit - $totalCredit;        if($totalPayable <0 ) {            $gl_data[] = array(                'ref_document_type_id' => $this->document_type_id,                'ref_document_id' => $work_order_id,                'ref_document_identity' => $data['document_identity'],                'coa_id' => $credit_account_id,                'document_debit' => 0,                'document_credit' => $totalPayable,                'debit' => (-1 * $totalPayable),                'credit' => 0            );        } else {            $gl_data[] = array(                'ref_document_type_id' => $this->document_type_id,                'ref_document_id' => $work_order_id,                'ref_document_identity' => $data['document_identity'],                'coa_id' => $credit_account_id,                'document_debit' => 0,                'document_credit' => $totalPayable,                'debit' => 0,                'credit' => $totalPayable            );        }        $this->model['ledger'] = $this->load->model('gl/ledger');        //d($gl_data, true);        foreach($gl_data as $sort_order => $ledger) {            $ledger['company_id'] = $this->session->data['company_id'];            $ledger['company_branch_id'] = $this->session->data['company_branch_id'];            $ledger['fiscal_year_id'] = $this->session->data['fiscal_year_id'];            $ledger['document_type_id'] = $this->document_type_id;            $ledger['document_id'] = $work_order_id;            $ledger['document_identity'] = $data['document_identity'];            $ledger['document_date'] = $data['document_date'];            $ledger['sort_order'] = $sort_order;            $ledger['base_currency_id'] = $data['base_currency_id'];            $ledger['document_currency_id'] = $data['document_currency_id'];            $ledger['conversion_rate'] = $data['conversion_rate'];            $ledger['people_type_id'] = 1;            $ledger['people_id'] = $data['supplier_id'];            $ledger['remarks'] = '';            $ledger_id = $this->model['ledger']->add($this->getAlias(), $ledger);        }        return $work_order_id;    }    protected function updateRedirect($id, $data) {        $url = $this->getURL();        $this->redirect($this->url->link($this->getAlias().'/update', 'token=' . $this->session->data['token'] . '&work_order_id=' . $id, 'SSL'));    }    protected function validateDelete() {        if (!$this->user->hasPermission('delete', $this->getAlias())) {            $this->error['warning'] = $this->language->get('error_permission_delete');        }        if (!$this->error) {            return true;        } else {            return false;        }    }    protected function deleteData($primary_key) {        $this->model['work_order_commodity'] = $this->load->model('vehicle/work_order_commodity');        $this->model['work_order_commodity']->deleteBulk($this->getAlias(),array('work_order_id' => $primary_key));        $this->model['work_order_vehicle'] = $this->load->model('vehicle/work_order_vehicle');        $this->model['work_order_vehicle']->deleteBulk($this->getAlias(),array('work_order_id' => $primary_key));        $this->model[$this->getAlias()]->delete($this->getAlias(), $primary_key);        $this->model['ledger'] = $this->load->model('gl/ledger');        $this->model['ledger']->deleteBulk($this->getAlias(), array('company_id' => $this->session->data['company_id'],'document_type_id' => $this->document_type_id, 'document_id' => $primary_key));    }}?>